CREATE TABLE jobeet_affiliate (id INTEGER PRIMARY KEY AUTOINCREMENT, url VARCHAR(255) NOT NULL, email VARCHAR(255) NOT NULL UNIQUE, token VARCHAR(255) NOT NULL, is_active INTEGER DEFAULT '0' NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
CREATE TABLE jobeet_category (id INTEGER PRIMARY KEY AUTOINCREMENT, name VARCHAR(255) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, slug VARCHAR(255));
CREATE TABLE jobeet_category_affiliate (category_id INTEGER, affiliate_id INTEGER, PRIMARY KEY(category_id, affiliate_id));
CREATE TABLE jobeet_job (id INTEGER PRIMARY KEY AUTOINCREMENT, category_id INTEGER NOT NULL, type VARCHAR(255), company VARCHAR(255) NOT NULL, logo VARCHAR(255), url VARCHAR(255), position VARCHAR(255) NOT NULL, location VARCHAR(255) NOT NULL, description VARCHAR(4000) NOT NULL, how_to_apply VARCHAR(4000) NOT NULL, token VARCHAR(255) NOT NULL UNIQUE, is_public INTEGER DEFAULT '1' NOT NULL, is_activated INTEGER DEFAULT '0' NOT NULL, email VARCHAR(255) NOT NULL, expires_at DATETIME NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
CREATE UNIQUE INDEX jobeet_category_sluggable_idx ON jobeet_category (slug);
